mapper.select:
SELECT ai.id, ai.softwareName, ai.APKName, ai.softwareSize, ai.flatformId, ai.categoryLevel1, ai.categoryLevel2, ai.categoryLevel3, ai.status, ai.downloads, av.versionNo, ai.versionId, dd.valueName flatformName, ddy.valueName statusName, ac1.categoryName categoryLevel1Name, ac2.categoryName categoryLevel2Name, ac3.categoryName categoryLevel3Name, ai.creationDate FROM app_info ai LEFT JOIN app_version av ON ai.id=av.appId JOIN data_dictionary dd ON ai.flatformId=dd.valueId AND dd.typeCode='APP_FLATFORM' JOIN data_dictionary ddy ON ai.status=ddy.valueId AND ddy.typeCode='APP_STATUS' JOIN app_category ac1 ON ai.categoryLevel1=ac1.id JOIN app_category ac2 ON ai.categoryLevel2=ac2.id JOIN app_category ac3 ON ai.categoryLevel3=ac3.id
and softwareName like concat('%',#{softwareName},'%')
and status = #{status}
and flatformId = #{flatformId}
and categoryLevel1 = #{categoryLevel1}
and categoryLevel2 = #{categoryLevel2}
and categoryLevel3 = #{categoryLevel3}
GROUP BY ai.id ORDER BY ai.creationDate DESC LIMIT #{pageIndex},#{pageSize}

SELECT COUNT(1) FROM app_info


        and softwareName like concat('%',#{softwareName},'%')


        and status = #{status}


        and flatformId = #{flatformId}


        and categoryLevel1 = #{categoryLevel1}


        and categoryLevel2 = #{categoryLevel2}


        and categoryLevel3 = #{categoryLevel3}

SELECT id,categoryName FROM app_category




            parentId=#{parentId}



                parentId IS NULL
