mapper.select:
SELECT ai.*, dd.valueName flatformName, ac1.categoryName categoryLevel1Name, ac2.categoryName categoryLevel2Name, ac3.categoryName categoryLevel3Name, ddy.valueName statusName, av.versionNo FROM app_info ai LEFT JOIN app_version av ON ai.id=av.appId JOIN data_dictionary dd ON ai.flatformId=dd.valueId AND dd.typeCode='APP_FLATFORM' JOIN data_dictionary ddy ON ai.status=ddy.valueId AND ddy.typeCode='APP_STATUS' JOIN app_category ac1 ON ai.categoryLevel1=ac1.id JOIN app_category ac2 ON ai.categoryLevel2=ac2.id JOIN app_category ac3 ON ai.categoryLevel3=ac3.id
and softwareName like concat('%',#{softwareName},'%')
and ai.flatformId=#{flatformId}
and ai.status=#{status}
and ai.categoryLevel1=#{categoryLevel1}
and ai.categoryLevel2=#{categoryLevel2}
and ai.categoryLevel3=#{categoryLevel3}
GROUP BY ai.id ORDER BY ai.softwareSize LIMIT #{pageIndex},#{pageSize}

SELECT COUNT(1) FROM app_info ai LEFT JOIN app_version av ON ai.id=av.appId
JOIN data_dictionary dd ON ai.flatformId=dd.valueId AND dd.typeCode='APP_FLATFORM'
JOIN data_dictionary ddy ON ai.status=ddy.valueId AND ddy.typeCode='APP_STATUS'
JOIN app_category ac1 ON ai.categoryLevel1=ac1.id
JOIN app_category ac2 ON ai.categoryLevel2=ac2.id
JOIN app_category ac3 ON ai.categoryLevel3=ac3.id


        and softwareName like concat('%',#{softwareName},'%')


        and ai.flatformId=#{flatformId}


        and ai.status=#{status}


        and ai.categoryLevel1=#{categoryLevel1}


        and ai.categoryLevel2=#{categoryLevel2}


        and ai.categoryLevel3=#{categoryLevel3}
